  // Advertencia si el evento ya pas√≥
  if (diasRestantes < 0) {
    console.warn(`‚ö†Ô∏è ADVERTENCIA: El evento ya pas√≥ (hace ${Math.abs(diasRestantes)} d√≠as)`);
  }

  // Retornar datos enriquecidos
  return {
    ...input,
    dias_del_evento: diasRestantes,
    timestamp_solicitud: timestampSolicitud,
    fecha_procesamiento: fechaProcesamiento,
    // Metadata adicional (comentar si no se necesita)
    _metadata_calculo: metadata
  };

} catch (error) {
  // Manejo de errores robusto
  console.error('‚ùå Error en calcularDias:', error.message);

  // Re-lanzar el error para que N8N lo maneje
  // (esto har√° que el workflow tome la rama de error si est√° configurada)
  throw new Error(`Error calculando d√≠as del evento: ${error.message}`);
}

/**
 * ============================================
 * NODO: clasificarUrgencia
 * ============================================
 * 
 * PROP√ìSITO:
 * Clasifica la urgencia de una solicitud de evento seg√∫n m√∫ltiples criterios
 * de negocio para priorizar el seguimiento comercial.
 * 
 * INPUT (desde nodo anterior):
 * - dias_del_evento: number - D√≠as restantes hasta el evento
 * - paquete_interes: string - Paquete seleccionado por el cliente
 * - tipo_evento: string - Categor√≠a del evento
 * 
 * OUTPUT:
 * - nivel_urgencia: string - Descripci√≥n del nivel (ej: "üî¥ ALTA (Menos de 1 semana)")
 * - emoji_urgencia: string - Emoji visual para identificaci√≥n r√°pida
 * 
 * L√ìGICA DE CLASIFICACI√ìN:
 * 1. ALTA (üî¥): Eventos pr√≥ximos (<7 d√≠as), clientes Enterprise, corporativos urgentes
 * 2. MEDIA (üü°): Eventos cercanos (<30 d√≠as), paquetes Premium
 * 3. NORMAL (üü¢): Resto de casos
 * 
 * AUTOR: Live Moments Team
 * √öLTIMA ACTUALIZACI√ìN: 2025-12-03
 */

// ============================================
// CONFIGURACI√ìN Y CONSTANTES
// ============================================

const CONFIG = {
  // Umbrales de d√≠as para clasificaci√≥n
  UMBRALES_DIAS: {
    ALTA_URGENCIA: 7,
    MEDIA_URGENCIA: 30,
    CORPORATIVO_URGENTE: 14
  },

  // Tipos de paquetes
  PAQUETES: {
    BASICO: 'B√°sico',
    ESTANDAR: 'Est√°ndar',
    PREMIUM: 'Premium',
    ENTERPRISE: 'Enterprise'
  },

  // Tipos de eventos especiales
  EVENTOS_ESPECIALES: {
    CORPORATIVO: 'Conferencias y eventos corporativos'
  },

  // Niveles de urgencia
  NIVELES: {
    ALTA: {
      emoji: 'üî¥',
      prefijo: 'üî¥ ALTA'
    },
    MEDIA: {
      emoji: 'üü°',
      prefijo: 'üü° MEDIA'
    },
    NORMAL: {
      emoji: 'üü¢',
      prefijo: 'üü¢ Normal'
    }
  }
};

// ============================================
// FUNCIONES AUXILIARES
// ============================================

/**
 * Determina si el evento es de tipo corporativo
 * @param {string} tipoEvento - Tipo de evento
 * @returns {boolean}
 */
function esCorporativo(tipoEvento) {
  return tipoEvento === CONFIG.EVENTOS_ESPECIALES.CORPORATIVO;
}

/**
 * Determina si el paquete es Enterprise
 * @param {string} paquete - Paquete seleccionado
 * @returns {boolean}
 */
function esEnterprise(paquete) {
  return paquete === CONFIG.PAQUETES.ENTERPRISE;
}

/**
 * Determina si el paquete es Premium
 * @param {string} paquete - Paquete seleccionado
 * @returns {boolean}
 */
function esPremium(paquete) {
  return paquete === CONFIG.PAQUETES.PREMIUM;
}

/**
 * Clasifica la urgencia seg√∫n d√≠as restantes
 * @param {number} dias - D√≠as hasta el evento
 * @param {string} paquete - Paquete seleccionado
 * @param {string} tipoEvento - Tipo de evento
 * @returns {Object} {nivel, emoji, razon}
 */
function clasificarPorCriterios(dias, paquete, tipoEvento) {
  // CRITERIO 1: Menos de 7 d√≠as (ALTA URGENCIA)
  if (dias < CONFIG.UMBRALES_DIAS.ALTA_URGENCIA) {
    return {
      nivel: CONFIG.NIVELES.ALTA.prefijo,
      emoji: CONFIG.NIVELES.ALTA.emoji,
      razon: 'Menos de 1 semana'
    };
  }

  // CRITERIO 2: Cliente Enterprise (ALTA URGENCIA)
  if (esEnterprise(paquete)) {
    return {
      nivel: CONFIG.NIVELES.ALTA.prefijo,
      emoji: 'üíé', // Emoji especial para Enterprise
      razon: 'Cliente Enterprise'
    };
  }

  // CRITERIO 3: Evento corporativo pr√≥ximo (ALTA URGENCIA)
  if (esCorporativo(tipoEvento) && dias < CONFIG.UMBRALES_DIAS.CORPORATIVO_URGENTE) {
    return {
      nivel: CONFIG.NIVELES.ALTA.prefijo,
      emoji: 'üè¢', // Emoji especial para corporativo
      razon: 'Corporativo pr√≥ximo'
    };
  }

  // CRITERIO 4: Menos de 30 d√≠as (MEDIA URGENCIA)
  if (dias < CONFIG.UMBRALES_DIAS.MEDIA_URGENCIA) {
    return {
      nivel: CONFIG.NIVELES.MEDIA.prefijo,
      emoji: CONFIG.NIVELES.MEDIA.emoji,
      razon: 'Menos de 1 mes'
    };
  }

  // CRITERIO 5: Paquete Premium (MEDIA URGENCIA)
  if (esPremium(paquete)) {
    return {
      nivel: CONFIG.NIVELES.MEDIA.prefijo,
      emoji: '‚≠ê', // Emoji especial para Premium
      razon: 'Paquete Premium'
    };
  }

  // CRITERIO 6: Resto de casos (NORMAL)
  return {
    nivel: CONFIG.NIVELES.NORMAL.prefijo,
    emoji: CONFIG.NIVELES.NORMAL.emoji,
    razon: 'Tiempo suficiente'
  };
}

// ============================================
// L√ìGICA PRINCIPAL
// ============================================

// Obtener datos del input
const input = $input.item.json;

// Extraer variables necesarias
const dias = input.dias_del_evento;
const paquete = input.paquete_interes;
const tipoEvento = input.tipo_evento;

// Validar que tenemos los datos necesarios
if (typeof dias !== 'number' || !paquete || !tipoEvento) {
  throw new Error('Faltan datos requeridos para clasificar urgencia');
}

// Clasificar urgencia
const clasificacion = clasificarPorCriterios(dias, paquete, tipoEvento);

// Construir descripci√≥n completa
const urgenciaCompleta = `${clasificacion.nivel} (${clasificacion.razon})`;

// Retornar datos enriquecidos
return {
  ...input,
  nivel_urgencia: urgenciaCompleta,
  emoji_urgencia: clasificacion.emoji,
  // Campos adicionales para debugging/analytics
  _metadata: {
    dias_clasificacion: dias,
    criterio_aplicado: clasificacion.razon,
    timestamp_clasificacion: new Date().toISOString()
  }
};

const input = $input.item.json;
const errores = [];

if (!input.nombre_cliente || input.nombre_cliente.length < 3) {
    errores.push("Nombre inv√°lido");
}
if (!input.email_cliente || !input.email_cliente.includes('@')) {
    errores.push("Email inv√°lido");
}
if (!input.telefono_cliente || input.telefono_cliente.length < 10) {
    errores.push("Tel√©fono inv√°lido");
}

return {
    ...input,
    datos_validos: errores.length === 0,
    lista_errores: errores
};

// ============================================
// NODO CODE - Procesar Respuesta del AI Agent
// ============================================
// Este nodo va DESPU√âS del AI Agent y ANTES de Gmail
// Obtener la respuesta del AI Agent
const aiOutput = $input.item.json.output;
// Obtener datos originales del formulario
const formData = $('formularioValido').item.json;
// ============================================
// 1. CONVERTIR ADD-ONS A HTML
// ============================================
let addonsHTML = '';
if (aiOutput.recomendaciones.addons && aiOutput.recomendaciones.addons.length > 0) {
  addonsHTML = aiOutput.recomendaciones.addons
    .map(addon => `<li>${addon}</li>`)
    .join('');
} else {
  // Si no hay add-ons, mostrar mensaje
  addonsHTML = '<li>No hay recomendaciones adicionales en este momento.</li>';
}
// ============================================
// 2. CONVERTIR PR√ìXIMOS PASOS A HTML
// ============================================
const proximosPasosHTML = aiOutput.proximos_pasos
  .map((paso, index) => `
    <tr>
      <td style="padding: 12px 0; border-bottom: 1px solid #eeeeee;">
        <table width="100%">
          <tr>
            <td width="40" style="vertical-align: top;">
              <div style="width: 32px; height: 32px; background: linear-gradient(135deg, #D4AF37 0%, #C5A028 100%); border-radius: 50%; color: white; font-weight: 700; font-size: 14px; text-align: center; line-height: 32px;">
                ${index + 1}
              </div>
            </td>
            <td style="color: #555555; font-size: 14px; line-height: 1.6; padding-left: 10px;">
              ${paso}
            </td>
          </tr>
        </table>
      </td>
    </tr>
  `)
  .join('');
// ============================================
// 3. GENERAR HTML PARA RECOMENDACI√ìN DE PAQUETE
// ============================================
let paqueteHTML = '';
if (aiOutput.recomendaciones.paquete) {
  paqueteHTML = `
    <table width="100%" cellpadding="0" cellspacing="0" style="background-color: #E8F5E9; border-left: 4px solid #4CAF50; border-radius: 4px; margin-bottom: 15px;">
      <tr>
        <td style="padding: 15px 20px;">
          <p style="margin: 0; color: #2E7D32; font-size: 14px; line-height: 1.6;">
            <strong>üì¶ Sobre tu paquete:</strong><br>
            ${aiOutput.recomendaciones.paquete}
          </p>
        </td>
      </tr>
    </table>
  `;
}
// ============================================
// 4. GENERAR HTML PARA CONSIDERACIONES
// ============================================
let consideracionesHTML = '';
if (aiOutput.recomendaciones.consideraciones) {
  consideracionesHTML = `
    <table width="100%" cellpadding="0" cellspacing="0" style="background-color: #E3F2FD; border-left: 4px solid #2196F3; border-radius: 4px; margin-bottom: 25px;">
      <tr>
        <td style="padding: 15px 20px;">
          <p style="margin: 0; color: #1565C0; font-size: 14px; line-height: 1.6;">
            <strong>‚ÑπÔ∏è Importante:</strong><br>
            ${aiOutput.recomendaciones.consideraciones}
          </p>
        </td>
      </tr>
    </table>
  `;
}
// ============================================
// 5. RETORNAR DATOS PROCESADOS
// ============================================
return {
  // Datos del AI procesados
  asunto: aiOutput.asunto,
  saludo: aiOutput.saludo,
  parrafo_confirmacion: aiOutput.parrafo_confirmacion,
  despedida: aiOutput.despedida,
  tono_detectado: aiOutput.tono_detectado,

  // HTML generado
  paquete_html: paqueteHTML,
  addons_html: addonsHTML,
  consideraciones_html: consideracionesHTML,
  proximos_pasos_html: proximosPasosHTML,

  // Datos originales del formulario (para el resumen)
  nombre_cliente: formData.nombre_cliente,
  email_cliente: formData.email_cliente,
  tipo_evento: formData.tipo_evento,
  fecha_evento: formData.fecha_evento,
  ubicacion_evento: formData.ubicacion_evento,
  paquete_interes: formData.paquete_interes,

  // Datos completos del AI (por si los necesitas)
  ai_response_completo: aiOutput
};

